import {FormikHelpers} from "formik";
import {useState,useEffect} from "react";

export const useCounterLogic = (step:number,counter:number,setCounter:(count :number)=>void):UseCounter  =>{
    const [start,setStart] = useState(false);

    useEffect(() => {
        if(start){
            const id = setInterval(() => {
                startToCount(); 
                console.log("test");

            }, 1000);
            console.log(id);

            return () => {clearInterval(id);console.log(id,"cleared");}

        }
      }, [start,counter]); // 

    const startToCount = ()=>{
            setCounter(parseFloat(counter.toString()) + parseFloat(step.toString()));
            setStart(true);
    }
    return {startToCount,counter}
}

interface UseCounter {
    counter:number;
    startToCount : ()=>void;
}
