import {FormikHelpers} from "formik";
import {useState,useEffect} from "react";

export const useCounterLogic = (step:number,counter:number,setCounter:(count :number)=>void):UseCounter  =>{


    // useEffect(() => {
    //     const id = setInterval(() => {
    //         startToCount()// 
    //     }, 1000);
    //     return () => clearInterval(id);/// to avoid conflict of time 
    //   }, [counter]); // 

    const startToCount = ()=>{
            setCounter(parseFloat(counter.toString()) + parseFloat(step.toString()))        
    }
    return {startToCount,counter}
}

interface UseCounter {
    counter:number;
    startToCount : ()=>void;
}
