import {FormikHelpers} from "formik";
import {useState,useEffect} from "react";

export const useCounterLogic = (step:number,count:number,setCount:(counter:number)=>void):UseCounter  =>{

    useEffect(() => {
       startToCount();
    }, [count]); // Only re-run the effect if counter changes

    const startToCount = ()=>{
        setTimeout(
            setCount(parseFloat(count.toString()) + parseFloat(step.toString()))
                , 3000);

    }
    return {startToCount}
}

interface UseCounter {

    startToCount : ()=>void;
}
