import { FormikHelpers } from "formik";
import { useState, useEffect, useRef } from "react";

export const useCounterLogic = (
  step: number,
): UseCounter => {
    const counterId = useRef(0); ///  useRef to initiate a state whichits value will initiate just the first render
    const [counter,setCounter]=useState(0); 
    const ceil =(num:number)=> Math.round((num + Number.EPSILON) * 100) / 100


  useEffect(() => {
    return () => {
      if (counterId.current != 0) {
        clearInterval(counterId.current);
      }
    };
  },[]);
  const count = () => {
    setCounter((_count)=>ceil(_count+step));

  };
  const startToCount = () => {

    if (counterId.current == 0) {
      counterId.current = setInterval(() => {
        count();
      }, 1000);
    }
  };
  return { startToCount, counter };
};

interface UseCounter {
  counter: number;
  startToCount: () => void;
}
