import {FormikHelpers} from "formik";
import {useState} from "react";



export const useCounters = ():UseCounters =>{
    const [loading,setLoading] = useState(false);
    const [stepValue,setStepValue] = useState([number]);

    const addCounter = (values: stepValueForm, helper: FormikHelpers<stepValueForm>)=>{
        try{
            setLoading(true);
            stepValue.push(values.stepValue);
            setStepValue(stepValue);

        }
        catch(e){
        }finally{
            setLoading(false);
            helper.setSubmitting(false);
        }
    }

    return{loading,addCounter,stepValue}
}
export interface stepValueForm { /// form inputs types
    stepValue : number;
}
interface UseCounters { /// useCounters hook output type
    loading :boolean;
    addCounter : (values: stepValueForm, helper: FormikHelpers<stepValueForm>)=>void;
    stepValue : number[];
}
