import styled from "styled-components";

export const CounterWrapper = styled.div`
    font-size : 5em;
    font-weight:bold;
    display:flex;
    flex-direction:row;
    justify-content : flex-start;
    margin :1em;
    align-items : center;

    & card {
        min-height : 10vw;
        min-width:10vw;
        margin : auto;
    } 

`
