import React from "react";
import {Button,Input,Layout,Col, Row,Form,Spin} from "antd";
import {Counter} from "./counter";
import {useCounters} from "./countersLogic";
import {FormHolder} from "./styled";
import { useFormik } from "formik";
import {stepValueForm} from "./countersLogic"


export const Counters = () =>{

    const {loading,addCounter,stepValue} = useCounters();
    const formik = useFormik<stepValueForm>({
        initialValues: {
            stepValue : 0
        },
        onSubmit: addCounter,
      });

    const { Header,  Content } = Layout;  /// destruct Header and Content from antd Layout 



    return (
    <Layout>
        <Header>
            <FormHolder>
                <form onSubmit={formik.handleSubmit}>
                    <Row gutter={16}  >                            
                        <Col span={8}   >
                            <Input
                                name={'stepValue'}
                                onChange={formik.handleChange}
                                value={formik.values.stepValue} 
                                placeholder={'please enter step value'} 
                            />
                        </Col>
                        <Col span={2}   >
                            <Button danger type="primary" htmlType={'submit'}  >
                                {loading ? <Spin /> : "Add Counter"}                                
                            </Button>
                        </Col>                
                    </Row>
                </form>
            </FormHolder>
        </Header>
        <Content>
            <Row gutter={16}>
                {stepValue.length > 0 && stepValue.map((step)=>(
                <Col span={8}>  
                    <Counter step={step} />
                </Col>
                ))}
            </Row>
        </Content>
    </Layout>
    )
}
