import React from "react";
import {Button,Input,Layout,Col, Row,Form,Alert} from "antd";
import {Counter} from "./counter";
import {FormHolder} from "./styled";
export const Counters = () =>{
    const { Header,  Content } = Layout;

    const onFinish = (values : number) => {
        console.log('Success:', values);
      };

      const onFinishFailed = (errorInfo:any) => {
        alert(
            <Alert message="Error" type="error" showIcon />
        )
      };

    return (
    <Layout>
        <Header>
            <FormHolder>
                <Form  onFinish={onFinish} onFinishFailed={onFinishFailed} > 
                    <Row gutter={16}  >                            
                        <Col span={8}   >
                        <Form.Item
                            name="stepValue"
                            rules={[{ required: true, message: 'Please input step value!' }]}
      >
                            <Input  placeholder={'please enter step value'} />
                        </Form.Item>    
                        </Col>
                        <Col span={2}   >
                        <Form.Item >
                            <Button danger type="primary" htmlType={'submit'}  >Add Counter</Button>
                        </Form.Item>    
                        </Col>                
                    </Row>
                </Form>
            </FormHolder>
        </Header>
        <Content>
            <Row gutter={16}>

                <Col span={8}>  
                    <Counter step={1} />
                </Col>

            </Row>
        </Content>
    </Layout>
    )
}
