import {FormikHelpers} from "formik";
import {useState,useEffect} from "react";

export const useCounterLogic = (step:number,count:number):UseCounter  =>{

    useEffect(() => {
       startToCount();
    }, [count]); // Only re-run the effect if counter changes

    const startToCount = ()=>{
        setTimeout(
            setCounter(parseFloat(counter.toString()) + parseFloat(step.toString()))
                , 3000);

    }
    return {startToCount,counter}
}

interface UseCounter {
    counter:number;
    startToCount : ()=>void;
}
