import {FormikHelpers} from "formik";
import {useState,useEffect} from "react";

export const useCounterLogic = (step:number,start:boolean):UseCounter  =>{
    const [counter,setCounter] = useState(0);

    useEffect(() => {
        startToCount();
    }, [counter,start]); // Only re-run the effect if counter changes

    const startToCount = ()=>{
        start= true;
        setCounter(parseFloat(counter.toString()) + parseFloat(step.toString()))
    }
    return {startToCount,counter}
}

interface UseCounter {
    counter:number;
    startToCount : ()=>void;
}
