import React from "react";
import { Card ,Button} from 'antd';
import {CounterWrapper} from "./styled";
import {useCounterLogic} from "./counterLogic";/// importing counter hook 
import {useState,useEffect} from "react";


export const Counter = (props:propsType)=>{
    const {step,id} = props; /// destructing props and catching step value
    const [counter,setCounter] = useState(0);
    const {startToCount} =useCounterLogic(step,counter,setCounter);

    useEffect(() => {
        const id = setInterval(() => {
            startToCount()// 
        }, 1000);
        return () => clearInterval(id);/// to avoid conflict of time 
      }, [counter]); // 

    return(
        <CounterWrapper>
            <Card title={"Step " + (id+1)} >
                <section>
                    <p>{counter}</p>   
                    <Button danger type="primary" block  onClick={()=>startToCount()} >Run</Button>
                </section>
            </Card>
        </CounterWrapper>   
    )
}

interface propsType {
    step :number;
    id :number;
}
